import React from 'react';
import './App.css';

class App extends React.Component {
  constructor () {
    super()
    this.handleClick = this.handleClick.bind(this) // Essa função esta ligada ao this.
    console.log("Componente sendo construido");
  }
  handleClick() {
    console.log('Clicou');
  }
  render() {
console.log(this);
    return (
    <div className="App">
     <button type='button' onClick={this.handleClick}> Botão 1</button>
     <button type='button'  onClick={this.handleClick}>Botão 2</button>
     <button type='button'  onClick={this.handleClick}>Botão 3</button>
    </div>
  );
}
}

export default App;
